sys_variable_value.value:
(function execute(inputs, outputs) {
  var payload = {};
  if (inputs.message) {
    payload = inputs.message;

    if (inputs.site) {
      // In this section mappings are done based on construction site record
      var siteObj = {
        "order.delivery.deliverylocation.id": inputs.site.site_id.toString(),
        "order.delivery.deliverylocation.description": inputs.site.additional_information.toString(),
        "order.delivery.deliverylocation.address.id": inputs.site.change_task.location.name.toString(),
        "order.delivery.deliverylocation.address.room": "",
        "order.delivery.deliverylocation.address.streetname": "",
        "order.delivery.deliverylocation.address.directions": "",
        "order.delivery.deliverylocation.address.cityname": "",
        "order.delivery.deliverylocation.address.postalzone": "",
        "order.delivery.deliverylocation.address.locationcoordinate.latitudex": "",
        "order.delivery.deliverylocation.address.locationcoordinate.longitudey": ""
      }
    }
    payload["order.delivery.deliverylocation"].push(siteObj);
    if (inputs.product) {
      // In this section mappings are done based on product(s) record(s)
      var productObj = {
        "order.orderline.note": inputs.product.task.short_description.toString() + " " + inputs.product.task.description.toString(),
        "order.orderline.lineitem.siteid": inputs.product.construction_site.site_id.toString(),
        "order.orderline.lineitem.id": "",
        "order.orderline.lineitem.quantity": "",
        "order.orderline.lineitem.item.id": "",
        "order.orderline.lineitem.item.description": "",
        "order.orderline.lineitem.item.name": "",
        "order.orderline.lineitem.item.additionalinformation": ""
      }
      payload["order.orderline"].push(productObj);
    }
  } else {
    payload = {
      "messageid": "",
      "order.profileid" : "Create",
      "order.id" : inputs.task.number.toString(),
      "order.issuedatetime" : inputs.task.u_work_order.number.toString(),
      "order.note" : "",
      "order.orderreference": "",
      "order.billingreference": "",
      "order.workingarea": "",
      "order.urgent": "",
      "order.documentreferences.document.id": "",
      "order.documentreferences.document.name": "",
      "order.worktype": "",
      "order.ordererparty.id": "",
      "order.ordererparty.contact.id": "",
      "order.ordererparty.contact.name": "",
      "order.ordererparty.contact.telephone": "",
      "order.ordererparty.contact.electronicmail": "",
      "order.customerparty.name": "",
      "order.customerparty.postaladdress.postbox": "",
      "order.customerparty.postaladdress.streetname": "",
      "order.customerparty.postaladdress.cityname": "",
      "order.customerparty.postaladdress.postalzone": "",
      "order.customerparty.contact.name": "",
      "order.customerparty.contact.telephone": "",
      "order.customerparty.contact.electronicmail": "",
      "order.delivery.deliverylocation": [],
      "order.delivery.requesteddeliveryperiod.startdate": "",
      "order.delivery.requesteddeliveryperiod.enddate": "",
      "order.delivery.technicalcontact.name": "",
      "order.delivery.technicalcontact.telephone": "",
      "order.delivery.technicalcontact.electronicmail": "",
      "order.orderline": []
    };
  }

  outputs.message = payload;
})(inputs, outputs);

