sys_script_client.script:
function onLoad() {
	//Type appropriate comment here, and begin script below
	if(g_form.getValue('planned_start_date') == '' || g_form.getValue('planned_end_date') == '') {
		var ref = g_form.getReference('task', callback);
	}

	function callback(t) {
		var ga = new GlideAjax('EmpowerWorkOrderAjax');
		ga.addParam('sysparm_name','getTask');
		ga.addParam('sysparm_table',t.sys_class_name);
		ga.addParam('sysparm_sys_id',t.sys_id);
		ga.getXML(getTaskParser);
	}

	function getTaskParser(response) {  
		var data = response.responseXML.documentElement.getAttribute("answer").split(','); 
//		alert(data[0]);
//		alert(data[1]);
		g_form.setValue('planned_start_date', data[0]);
		g_form.setValue('planned_end_date', data[1]);
	}
}
