sys_script_include.script:
var EmpowerWorkOrderAjax = Class.create();
EmpowerWorkOrderAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getTask: function () {
		var table = this.getParameter('sysparm_table');
		var sys_id = this.getParameter('sysparm_sys_id');
		var result;

		gs.info('EmpowerWorkOrderAjax.getTask | table: ' + table);
		gs.info('EmpowerWorkOrderAjax.getTask | sys_id: ' + sys_id);

		var gr = new GlideRecord(table.toString());
		gr.addQuery('sys_id', sys_id.toString());
		gr.query();
		if (gr.next()) {
			gs.info('EmpowerWorkOrderAjax.getTask | planned_start_date: ' + gr.planned_start_date);
			gs.info('EmpowerWorkOrderAjax.getTask | planned_end_date: ' + gr.planned_end_date);
			result = gr.planned_start_date.getDisplayValue();
			result += ',' + gr.planned_end_date.getDisplayValue();

		}
		gs.info('EmpowerWorkOrderAjax.getTask | result: ' + result);
		return result;
	},

	type: 'EmpowerWorkOrderAjax'
});
